import React, { Component } from 'react'
import './index.css'

export default class List extends Component {
    state = { sum: 0 }
    render() {
        return (
            <div>
                <h2>结果为 : {this.state.sum}</h2>

                <br />
                <select name="sum" id="2" ref={c => { this.valNode = c }}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>


                </select>&nbsp;
                <button onClick={this.add}> + </button> &nbsp;
                <button onClick={this.decrement}> - </button>&nbsp;
                <button onClick={this.addIfOdd}>奇数加</button>&nbsp;
                <button onClick={this.addLonger}>异步加</button>&nbsp;
            </div>
        )
    }
    add = () => {
        const { sum } = this.state
        const { value } = this.valNode
        this.setState({ sum: sum + value * 1 })

    }
    decrement = () => {
        const { sum } = this.state
        const { value } = this.valNode
        this.setState({ sum: sum - value * 1 })
    }

    addIfOdd = () => {
        const { sum } = this.state

        const { value } = this.valNode
        if (sum % 2 !== 0) {
            this.setState({ sum: sum - value * 1 })

        }
    }

        addLonger = () => {
            const { sum } = this.state

            const { value } = this.valNode

            setTimeout(() => { this.setState({ sum: sum + value * 1 }) }, 200)

        }
    }
