import React, { useEffect, useState } from 'react';
const MyContext = React.createContext();
const {Provider} =MyContext
const {Consumer} =MyContext









export function App() {

  return (
	<div>
	  <h2>我是A组件</h2>
	  			<Provider value={{name:'zjk',age:24,career:'ttp'}}>
					<B name='zjk' sex='male'/>
				</Provider>
	</div>
  )
}


export function B(props) {
	const [count,setCount]=useState(3)

	useEffect(() =>{
		function add(){

			setInterval(() => {
				setCount(count+1)
			}, 2000);

		}
	},[])


  return (
	<div>
	  <h2>我是B组件</h2>
	  <h3>我收到的来源于A组件的内容为{props.sex}</h3>
	  <h3>the inital value of count is {count}</h3>
	  <button onClick={add}>add</button>
	  <C/>
	</div>
  )
}


export function C() {
  return (
	<div>
	  <h2>我是C组件</h2>
	  <h3>
		<Consumer>
			{
				value=>{
					return`我从A组件中接收到的名字为${value.name},年龄为 ${value.age} 职业为${value.career}`
				}
			}
		</Consumer>
	  </h3>
	</div>
  )
}
