import React, { Component } from 'react';
const MyContext = React.createContext();
const {Provider} =MyContext

export class App extends Component {
	state={name:'zjk',age:25}
	render() {
		return (
			<div>
				<h2>我是A组件</h2>
				<Provider value={{name:'dn',age:18}}>
				<B/>
				</Provider>

			</div>
		);
	}
}





export  class B extends Component {
	render() {
		return (
			<div>
				<h3>我是B组件</h3>
				<C/>
			</div>
		)
	}
}
export  class C extends Component {
	//声明接收
	static contextType=MyContext
	render() {
		return (
			<div>
				<h3>我是C组件</h3>
				<h4>我是从A组件收到的用户名:{this.context.name}</h4>
				<h4>我是从A组件收到的年龄:{this.context.age}</h4>
			</div>
		);
	}
}





