import React, { useEffect, useRef, useState } from 'react'







export default function App() {
	const [name, setName] = useState('Jack')
	const [age, setAge] = useState(1)
	const myRef=useRef()

	useEffect(() => {
		let timer = setInterval(() => {
			setAge(age + 1)
		}, 2000)
		return () => {
			clearInterval(timer)
		}
	}, [])

	function change() {
		if (name === 'Jack') setName('JK')
		if (name === 'JK') setName('Jack')
	}

function show(){
	alert("content is ",myRef.current.value)
	console.log(myRef.current.value);
}


	return (
		<div>
			<h2>{name}</h2>
			<h3>{age}</h3>
			<button >add one</button>
			<button onClick={change}>change Name</button>
			<input type="text" ref={myRef} onKeyDown={show}/>

		</div>
	)
}
