/* 
	该文件为Count的UI组件：
			1.不允许写任何redux相关的api
			2.它会等得到其容器组件(父组件)传递过来的：状态、操作状态的方法
*/
import React, { Component } from 'react'

export default class Count extends Component {

	state = {
		school:'尚硅谷'
	}

	//加
	increment = ()=>{
		//1.获取用户输入
		const {value} = this.myref
		//2.通知redux加value

		this.props.increment(value*1)
	}

	//减
	decrement = ()=>{
		//1.获取用户输入
		const {value} = this.myref
		//2.通知redux减value
		this.props.decrement(value*1)
	}

	//当前求和为奇数，再加
	incrementIfOdd = ()=>{
		if(this.props.count % 2 !== 0){
			//1.获取用户输入
			const {value} = this.myref
			//2.通知redux加value
			this.props.increment(value*1)
		}
	}

	//等一等再加
	incrementWait = ()=>{
		//1.获取用户输入
		const {value} = this.myref
		//2.通知redux减value
		this.props.incrementWait(value*1)
	}

	render() {
		// console.log('CountUI组件接收到的props:',this.props);
		return (
			<div>
				<h1>{this.state.school}-当前求和为:{this.props.count}</h1>
				<select ref={(c)=>{this.myref=c}}>
					<option value="1">1</option>
					<option value="2">2</option>
					<option value="3">3</option>
				</select>&nbsp;
				<button onClick={this.increment}>+</button>&nbsp;
				<button onClick={this.decrement}>-</button>&nbsp;
				<button onClick={this.incrementIfOdd}>若和为奇数 +</button>&nbsp;
				<button onClick={this.incrementWait}>等一等 +</button>&nbsp;
			</div>
		)
	}
}
